    //leetcode submit region begin(Prohibit modification and deletion)
    class Solution {
        public int findLength(int[] A, int[] B) {
            int max = 0;
            int[][] maxLength = new int[A.length][B.length];
            IntStream.range(0, A.length).forEach(i -> IntStream.range(0, B.length).forEach(j -> maxLength[i][j] = -1));
            for (int i = 0; i < A.length; i++) {
                for (int j = 0; j < B.length; j++) {
                    max = Math.max(max, this.findLength(A, i, B, j, maxLength));
                }
            }
            return max;
        }

        public int findLength(int[] A, int i, int[] B, int j, int[][] maxLength) {
            if (maxLength[i][j] > -1) {
                return maxLength[i][j];
            }
            if (A[i] == B[j]) {
                maxLength[i][j] = 1 + ((i == A.length - 1 || j == B.length - 1) ? 0 : this.findLength(A,
                        i + 1, B, j + 1, maxLength));
            } else {
                maxLength[i][j] = 0;
            }
            return maxLength[i][j];
        }
    }
//leetcode submit region end(Prohibit modification and deletion)

